import "./App.css";
import { useState } from "react";
import { LC, NC, SC, UC } from "./data/PassChar";
import { toast, ToastContainer } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';

function App() {
  const [upperCase, setUpperCase] = useState(false);
  const [lowercase, setLowercase] = useState(false);
  const [numbers, setNumbers] = useState(false);
  const [symbol, setSymbol] = useState(false);
  const [passlen, setPassLen] = useState(10);
  const [Password,setPassword] = useState("");
  let createPassword = () => {
    let charSet = "";
    let finalPass = "";
    if (upperCase || lowercase || numbers || symbol) {
      if (upperCase) charSet += UC;
      if (lowercase) charSet += LC;
      if (numbers) charSet += NC;
      if (symbol) charSet += SC;

      for (let i = 0; i < passlen; i++) {
        finalPass += charSet.charAt(Math.floor(Math.random() * charSet.length));
      }
     setPassword(finalPass);
    } else {
      alert("Ek Checkbox to select kro Sahab!!");
    }
  };

 let Copy = ()=>{
  if(!Password===''){
    navigator.clipboard.writeText(Password);
  toast.success("Password has been copied to your Clipboard");
  }else{
    toast.error("Genrate Password ")
  }
 }




  return (
    <div>
      <ToastContainer/>
      <div className="passwordBox">
        <h3>Password Generator</h3>
        <div className="password">
          <input value={Password} readonly/>
          <button onClick={Copy}>Copy</button>
        </div>

        <div className="passlength">
          <label>Password length</label>
          <input
            type="number"
            max={20}
            min={1}
            value={passlen}
            onChange={(e) => setPassLen(e.target.value)}
          />
        </div>
        <div className="passlength">
          <label>Include Uppercase</label>
          <input
            type="checkbox"
            checked={upperCase}
            onChange={() => setUpperCase(!upperCase)}
          />
        </div>
        <div className="passlength">
          <label>Include lowercase</label>
          <input
            type="checkbox"
            checked={lowercase}
            onChange={() => setLowercase(!lowercase)}
          />
        </div>
        <div className="passlength">
          <label>Include numbers</label>
          <input
            type="checkbox"
            checked={numbers}
            onChange={() => setNumbers(!numbers)}
          />
        </div>
        <div className="passlength">
          <label>Include symbol</label>
          <input
            type="checkbox"
            checked={symbol}
            onChange={() => setSymbol(!symbol)}
          />
        </div>
        <button onClick={createPassword} className="genratePass">
          Generate Password
        </button>
      </div>
    </div>
  );
}

export default App;
