import "./App.css";
import { useState } from "react";
import { LC, NC, SC, UC } from "./data/PassChar";

function App() {
  const [upperCase,setUpperCase]=useState(false);
  const [lowercase,setLowercase]=useState(false);
  const [numbers,setNumbers]=useState(false);
  const [symbol,setSymbol]=useState(false);
  const [passlen,setPassLen]=useState(10);

  let createPassword = ()=>{
    let charSet = '';
    let finalPass = '';
    if(upperCase || lowercase || numbers || symbol){
      if(upperCase) charSet+= UC;
      if(lowercase) charSet+= LC;
      if(numbers) charSet+= NC;
      if(symbol) charSet+= SC;

 console.log(passlen);

      for(let i=0;i<passlen;i++){
        let finalPass = charSet.charAt(Math.floor(Math.random()*charSet.length));
      }
      console.log(finalPass);
    }else{
      alert("Ek Checkbox to select kro Sahab!!")
    }
  }

  return (
    <div>
      <div className="passwordBox">
        <h3>Password Generator</h3>
        <div className="password">
          <input />
          <button>Copy</button>
        </div>

        <div className="passlength">
          <label>Password length</label>
          <input type="number" max={20} min={1}   value={passlen} onChange={(e)=>setPassLen(e.target.value)}/>
        </div>
        <div className="passlength">
          <label>Include Uppercase</label>
          <input type="checkbox" checked={upperCase} onChange={()=>setUpperCase(!upperCase)}/>
        </div>
        <div className="passlength">
          <label>Include lowercase</label>
          <input type="checkbox"checked={lowercase} onChange={()=>setLowercase(!lowercase)} />
        </div>
        <div className="passlength">
          <label>Include numbers</label>
          <input type="checkbox" checked={numbers} onChange={()=>setNumbers(!numbers)} />
        </div>
        <div className="passlength">
          <label>Include symbol</label>
          <input type="checkbox"checked={symbol} onChange={()=>setSymbol(!symbol)} />
        </div> 
        <button onClick={createPassword} className="genratePass">Generate Password</button>
      </div>
    </div>
  );
}

export default App;
